import Foundation

public extension Store {

	/// Wrapper around methods that modify the state. Actions are generated by `@Actions` macro.
	struct Action<Args, Res>: Identifiable, Sendable {

		public let id: StoreActionID
		public var name: String { id.name }
		let action: @Sendable @MainActor (Store<State>, Args) -> Res

		public init(
			id: StoreActionID,
			action: @escaping @Sendable @MainActor (Store<State>, Args) -> Res
		) {
			self.id = id
			self.action = action
		}
	}
}

public extension Store.Action {

	typealias Throws = Store.Action<Args, Result<Res, Error>>
	typealias Async = Store.Action<Args, Task<Res, Never>>
	typealias AsyncThrows = Store.Action<Args, Task<Res, Error>>
}

extension Store.Action: CustomStringConvertible {

	public var description: String {
		"Store<\(State.self)>.\(id.name)"
	}
}

extension Store.Action: CustomDebugStringConvertible {

	public var debugDescription: String {
		"Store<\(State.self)>.Action<\(Args.self), \(Res.self)>.\(id)"
	}
}

public extension Store.Action where Res == Void {

	typealias Return<T> = Store.Action<Args, T>
}

public extension Store.Action {

	init(
		id: StoreActionID,
		action: @escaping (Store<State>) -> @MainActor (Args) -> Res
	) {
		self.init(id: id) { store, args in
			store.update {
				action(store)(args)
			}
		}
	}

	init<T>(
		id: StoreActionID,
		cancelInFlight: Bool = false,
		action: @escaping @Sendable (Store<State>) -> @MainActor (Args) async -> T
	) where Res == Task<T, Never> {
		self.init(id: id) { store, args in
			store.task(id: id, cancelInFlight: cancelInFlight) {
				await action(store)(args)
			}
		}
	}

	init<T>(
		id: StoreActionID,
		cancelInFlight: Bool = false,
		action: @escaping @Sendable (Store<State>) -> @MainActor (Args) async throws -> T
	) where Res == Task<T, Error> {
		self.init(id: id) { store, args in
			store.task(id: id, cancelInFlight: cancelInFlight) {
				try await action(store)(args)
			}
		}
	}

	init<T>(
		id: StoreActionID,
		action: @escaping (Store<State>) -> @MainActor (Args) throws -> T
	) where Res == Result<T, Error> {
		self.init(id: id) { store, args in
			store.update {
				Result {
					try action(store)(args)
				}
			}
		}
	}
}

public extension Store.Action {

	/// Action execution context.
	struct Context: Hashable {

		/// Action identifier.
		public let actionID: StoreActionID
		/// File ID of the execution call.
		public let file: String
		/// Line number of the execution call.
		public let line: UInt
		/// Function name where execution call was made.
		public let function: String

		public init(
			actionID: StoreActionID,
			file: String,
			line: UInt,
			function: String
		) {
			self.actionID = actionID
			self.file = file
			self.line = line
			self.function = function
		}
	}
}

@MainActor
public extension Store {

	/// Executes the given action through middlwares.
	func execute<Args, Res>(
		_ action: Action<Args, Res>,
		with args: Args,
		file: String,
		line: UInt,
		from function: String
	) -> Res {
		let closure = {
			action.action(self, $0)
		}
		return di.middlewares.execute(
			args,
			context: Store<State>.Action<Args, Res>.Context(
				actionID: action.id,
				file: file,
				line: line,
				function: function
			),
			dependencies: di
		) { args in
			closure(args)
		}
	}

	/// Executes the given action through middlwares.
	func execute<Args, Res>(
		_ action: Action<Args, Result<Res, Error>>,
		with args: Args,
		file: String,
		line: UInt,
		from function: String
	) throws -> Res {
		let closure = {
			action.action(self, $0)
		}
		return try di.middlewares.executeThrows(
			args,
			context: Store<State>.Action<Args, Res>.Throws.Context(
				actionID: action.id,
				file: file,
				line: line,
				function: function
			),
			dependencies: di
		) { args in
			closure(args)
		}
		.get()
	}

	/// Executes the given action through middlwares and make it cancellable by its id.
	func execute<Args, Res>(
		_ action: Action<Args, Task<Res, Never>>,
		with args: Args,
		file: String,
		line: UInt,
		from function: String
	) async -> Res {
		let closure = {
			action.action(self, $0)
		}
		return await di.middlewares.executeAsync(
			args,
			context: Store<State>.Action<Args, Res>.Async.Context(
				actionID: action.id,
				file: file,
				line: line,
				function: function
			),
			dependencies: di
		) { args in
			closure(args)
		}
		.value
	}

	/// Executes the given action through middlwares and make it cancellable by its id.
	func execute<Args, Res>(
		_ action: Action<Args, Task<Res, Error>>,
		with args: Args,
		file: String,
		line: UInt,
		from function: String
	) async throws -> Res {
		let closure = {
			action.action(self, $0)
		}
		return try await di.middlewares.executeAsyncThrows(
			args,
			context: Store<State>.Action<Args, Res>.AsyncThrows.Context(
				actionID: action.id,
				file: file,
				line: line,
				function: function
			),
			dependencies: di
		) { args in
			closure(args)
		}
		.value
	}

	/// Executes the given closure through middlwares.
	func execute<Res>(
		id: StoreActionID? = nil,
		file: String = #fileID,
		line: UInt = #line,
		from function: String = #function,
		action: @MainActor @escaping () -> Res
	) -> Res {
		execute(
			Action<Void, Res>(
				id: id ?? StoreActionID(name: "anonymous", fileID: file, line: line),
				action: { _, _ in
					action()
				}
			),
			with: (),
			file: file,
			line: line,
			from: function
		)
	}

	/// Executes the given closure through middlwares.
	func execute<Res>(
		id: StoreActionID? = nil,
		file: String = #fileID,
		line: UInt = #line,
		from function: String = #function,
		action: @MainActor @escaping () throws -> Res
	) throws -> Res {
		try execute(
			Action<Void, Result<Res, Error>>(
				id: id ?? StoreActionID(name: "anonymous", fileID: file, line: line),
				action: { _ in
					{ _ in try action() }
				}
			),
			with: (),
			file: file,
			line: line,
			from: function
		)
	}

	/// Executes the given closure through middlwares and make it cancellable by its id.
	func execute<Res>(
		id: StoreActionID? = nil,
		file: String = #fileID,
		line: UInt = #line,
		from function: String = #function,
		cancelInFlight: Bool = false,
		action: @MainActor @escaping () async throws -> Res
	) async throws -> Res {
		try await execute(
			Action<Void, Task<Res, Error>>(
				id: id ?? StoreActionID(name: "anonymous", fileID: file, line: line),
				cancelInFlight: cancelInFlight,
				action: { _ in
					{ _ in try await action() }
				}
			),
			with: (),
			file: file,
			line: line,
			from: function
		)
	}

	/// Executes the given closure through middlwares and make it cancellable by its id.
	func execute<Res>(
		id: StoreActionID? = nil,
		file: String = #fileID,
		line: UInt = #line,
		from function: String = #function,
		cancelInFlight: Bool = false,
		action: @MainActor @escaping () async -> Res
	) async -> Res {
		await execute(
			Action<Void, Task<Res, Never>>(
				id: id ?? StoreActionID(name: "anonymous", fileID: file, line: line),
				cancelInFlight: cancelInFlight,
				action: { _ in
					{ _ in await action() }
				}
			),
			with: (),
			file: file,
			line: line,
			from: function
		)
	}
}

/// Store action identifier.
public struct StoreActionID: Hashable, CustomStringConvertible, Sendable {

	/// Action name.
	public let name: String
	/// Declaration file ID.
	public let fileID: String
	/// Declaration line number.
	public let line: UInt

	public init(name: String, fileID: String = #fileID, line: UInt = #line) {
		self.name = name
		self.fileID = fileID
		self.line = line
	}

	public var description: String {
		"\(fileID):\(line) \(name)"
	}
}
